def format_dataframe_as_table(df):
    table = PrettyTable()
    table.field_names = df.columns
    for _, row in df.iterrows():
        table.add_row(row)
    return str(table)
def return_comics(chose_heroes):
    data_of_comics = []
    for i in chose_heroes:
        data_of_comics.extend((df[df["Characters"] == i]["Comics"].values[0].split(", ")))
    data_of_comics = list(set(data_of_comics))
    data_dict = {}
    dict_of_characters = []
    m = 0
    for i in chose_heroes:
        data_dict[i] = []
        dict_of_characters.append([])
        for j in data_of_comics:
            if j in df[df["Characters"] == i]["Comics"].values[0].split(", "):
                data_dict[i].append(1)
                dict_of_characters[m].append(i)
            else:
                data_dict[i].append(0)
                dict_of_characters[m].append("")
        m+=1
    dict_of_characters2=[]
    for j in range(len(dict_of_characters[0])):
        delimiter = ', '
        arr = []
        for i in range(len(dict_of_characters)):
            arr.append(dict_of_characters[i][j])
        dict_of_characters2.append(delimiter.join(filter(None, arr)))
    print(len(dict_of_characters2))
    print(dict_of_characters2)
    df2 = pd.DataFrame(data_dict)
    df2["Actice Characters"] = dict_of_characters2
    df2["Comic"] = data_of_comics
    df2['Relevance'] = df2[chose_heroes].sum(axis=1)
    recommended_comics = df2[df2["Relevance"] > 1].sort_values(by='Relevance', ascending=False)[["Actice Characters",'Comic']]
    # recommended_comics['Character_ID'] = recommended_comics['Actice Characters'].apply(
    #     lambda characters: [dict_of_characters[character.strip()] for character in characters.split(',')]
    # )
    #
    # recommended_comics['Comic_Link'] = recommended_comics.apply(lambda row: return_links(row['Character_ID'][0], row['Comic']), axis=1)
    # print(recommended_comics)
    return format_dataframe_as_table(recommended_comics)


PUBLIC_KEY = "PUBLIC_KEY"
PRIVATE_KEY = "PRIVATE_KEY"


def return_links(id):

    marvel = Marvel(PUBLIC_KEY, PRIVATE_KEY)
    comics = marvel.characters.comics(id)
    comic_links = {}

    for comic in comics['data']['results']:
        title = comic['title']
        links = comic['urls']
        comic_details = [f"{title}"]

        for link in links:
            if link['type'] == "detail":
                comic_details.append(f"{link['url']}")
                comic_links[title] = link['url']
    return comic_links

